import React, { Component } from 'react';
import './Bodyframe.css'
import backgroundFrame from "../../img/background_frame.png"

const imgStyle = {

    zIndex: '-1',

}


function mainBody() {
    return ( 
            <main id="contentArea">
              <section id="content">
                    <ul id="category">
                        <a id='ex'>
                            <li id="ex">Job Posts</li>
                        </a>
                        <li>Apply</li>
                        <li>More</li>
                    </ul>
                    <form id='searchbar'>
                        <input
                            type="text"
                            className="form-control"
                            placeholder="Search by title"
                            value={searchTitle}
                            onChange={this.onChangeSearchTitle}
                        />
                        <div className="input-group-append">
                            <button
                                className="btn btn-outline-secondary"
                                type="button"
                                onClick={this.searchTitle}
                            >
                                Search
                            </button>
                        </div>
                    </form>
                    <article id='jobboard'>
                        <div id='jobboardImage'>
                            <div className="col-md-12">
                                <h4>Job Posts</h4>

                                <ul className="list-group">
                                    {jobPosts &&
                                    jobPosts.map((jobPost, index) => (
                                        <li
                                        className={
                                            "list-group-item " +
                                            (index === currentIndex ? "active" : "")
                                        }
                                        onClick={() => {
                                            this.setActiveJobPost(jobPost, index)
                                        }}
                                        key={index}
                                        style={{color: 'black'}}
                                        >
                                        {jobPost.title}
                                        </li>
                                    ))}
                                </ul>

                                <button
                                    className="m-3 btn btn-sm btn-danger"
                                    onClick={this.removeAllJobPosts}
                                >
                                    Remove All
                                </button>
                                </div>
                        </div>
                        <div id="openPost" className="col-md-6">
                                {currentJobPost ? (
                                    <div>
                                    <h4>Job Post</h4>
                                    <div>
                                        <label>
                                        <strong>Title:</strong>
                                        </label>{" "}
                                        {currentJobPost.title}
                                    </div>
                                    <div>
                                        <label>
                                        <strong>Description:</strong>
                                        </label>{" "}
                                        {currentJobPost.description}
                                    </div>
                                    <div>
                                        <label>
                                        <strong>Job Type:</strong>
                                        </label>{" "}
                                        {currentJobPost.jobType}
                                    </div>

                                    <Link
                                        to={"/jobPosts/" + currentJobPost.id}
                                        className="badge badge-warning"
                                    >
                                        Edit
                                    </Link>
                                    </div>
                                ) : (
                                    <div>
                                    <br />
                                    <p>Please click on a JobPost...</p>
                                    </div>
                                )}
                          </div>
                    </article>
                </section>
            </main>
        
    );
}

export default mainBody;
